import {useEffect, useState } from 'react';
import { SearchBar } from './components/searchBar';
import { SearchResults } from './components/searchResults';
import axios from 'axios';
/* import { axios } from axios; */

const API = "http://localhost:3030/api";

function App() {
    const [results, setResults] = useState([]);
    const [edited, setEdited] = useState(false);

    const DeleteTrack = async id => {
        const data = await fetch(API + "/delete/" + id, { method: "DELETE" })
        .then(res => res.json());

        setResults(results => results.filter(track => track._id !== data._id))
    }

    const EditTrack = id => {
        fetch(API + "/update/" + id, { 
            method: 'PUT',
            headers: {
                'Content-type': 'application/json',
            },
            body: JSON.stringify(EditTrack)
        })
        .then ((response) => {
            return response.json(setEdit)
        })
        .then((data) => {
            console.log(data)
            setEdited(false);
        })
    }

    return (
        <div className="App">
            <header className="main-header">
                <h1>LDR MUSIC LIBRARY</h1>
               {<h2>Find your favorite songs</h2>}
            </header>
            <section className='search-section'>
                <SearchBar setResults={setResults} />
                <SearchResults results={results} deleteTrack={DeleteTrack} editTrack={EditTrack} />
            </section>
        </div>
    );
}

export default App;
