import React, { useState } from "react";
import "./singleSearchResult.css"
import { CheckImage } from "./checkImage";

export const OneSearchResult = ({ result, id, deleteTrack, editTrack}) => {
    const [isChecked, setIsChecked] = useState(result.checked)

    const handleCheckboxChange = e => {
        setIsChecked(!isChecked)
        editTrack(result.name)
    }

    return (
    <>  <div className="track">
            <div className="buttons">
                <div    
                    className="edit"
                    type="checkbox"
                    checked={isChecked}
                    onChange={handleCheckboxChange}
                    onClick={() => editTrack(id)}>
                    e
                </div>
                <div className="delete" onClick={() => deleteTrack(id)}>
                    x
                </div>
            </div>
            <CheckImage album={result.album}/>
            <div className='description'>
                <div>Title: {result.name}</div>
                <div>Artist: {result.artist}</div>
                <div>Album: {result.album}</div>
                <div>Year: {result.year}</div>
            </div>
        </div> 
    </>
    )
}
