import {useState, useEffect } from 'react';
import { SearchBar } from './components/searchBar';
import { SearchResults } from './components/searchResults';

const API = "http://localhost:3030/api";

function App() {
    const [tracks, setTracks] = useState([]);
    const [results, setResults] = useState([]);
   

    useEffect(() => {
        GetTracks();
        
    }, [])

    // haetaan kaikki kappaleet
    const GetTracks = () => {
        fetch(API + "/getall")
        .then(res => res.json())
        .then(data => setTracks(data))
        .catch(err => console.error("Error: ", err));
    }


    const DeleteTrack = async id => {
        const data = await fetch(API + "/delete/" + id, { method: "DELETE" })
        .then(res => res.json());

        setResults(results => results.filter(track => track._id !== data._id))
    }

    return (
        <div className="App">
            <header className="main-header">
                <h1>LDR MUSIC LIBRARY</h1>
               {<h2>Find your favorite songs</h2>}
            </header>

{/*             <nav className="navbar">
                    <ul className="links">
                        <li>Show all songs</li>
                        <li>Search & edit</li>
                        <li>Add new</li>
                    </ul>
            </nav> */}
            <section className='search-section'>
                <SearchBar setResults={setResults} />
                <SearchResults results={results} deleteTrack={DeleteTrack} />
            </section>
            {/* <section className='add'>
                    <h3>Add Track</h3> 
                <form>
                    <label>Title</label>
                    <input type='text'></input>
                    <label>Album</label>
                    <input type='text'></input>
                    <input 
                        type="submit" 
                        value="Submit" className='submit'></input>
                </form>
            </section> */}
            {/* <section className='content'>
                {tracks.map(track => (
                    <div className='track' key={track._id}>
                        <div className='delete' onClick={() => DeleteTrack (track._id)}>x</div> 
                        <CheckImage album={track.album}/>
                        <div className='description'>
                            <div>Title: {track.name}</div>
                            <div>Artist: {track.artist}</div>
                            <div>Album: {track.album}</div>
                            <div>Year: {track.year}</div>
                        </div>
                    </div>
                ))}
            </section> */}
        </div>

    );
}

export default App;
