import {useState, useEffect } from 'react';
const API = "http://localhost:3030/api";

function App() {
    const [tracks, setTracks] = useState([]);

    useEffect(() => {
        GetTracks();
        
    }, [])


    const GetTracks = () => {
        fetch(API + "/getall")
        .then(res => res.json())
        .then(data => setTracks(data))
        .catch(err => console.error("Error: ", err));
    }

/*     let albumCover;
    const checkImage = (props) => {
        if (props.album === "Born To Die") {
            albumCover = "borntodie.png";
        } else {
            albumCover = "honeymoon.png"
        }

        return (
            <img
                src={albumCover}
                alt="Album cover"
            />    
        )
    } */

    return (
        <div className="App">
            <header className="main-header">
                <h1>LDR MUSIC LIBRARY</h1>
                <h2>What would you like to do?</h2>
            </header>

            <nav className="navbar">
                    <ul className="links">
                        <li>Show all songs</li>
                        <li>Search & edit</li>
                        <li>Add new</li>
                    </ul>
            </nav>
            <section className='content'>
                {tracks.map(track => (
                    <div className='track' key={track._id}> 
                        <img src="borntodie.png" alt="Born To Die album cover"/>
                        <div className='description'>
                            <div>Title: {track.name}</div>
                            <div>Artist: {track.artist}</div>
                            <div>Album: {track.album}</div>
                            <div>Year: {track.year}</div><br></br>
                        </div>
                    </div>
                ))}
            </section>
        </div>

    );
}

export default App;
