import React, {useState} from "react";
import {FaSearch} from "react-icons/fa";
import "./searchBar.css";
import e from "express";

const API = "http://localhost:3030/api";

export const SearchBar = ({ setResults, setEdit }) => {
    const [input, setInput] = useState("")

    const fetchData = (value) => {
        fetch (API + "/getall")
            .then((response) => response.json())
            .then(json => {
                const results = json.filter((track) => {
                    return (
                        value &&
                        track && 
                        track.name &&
                        track.name.toLowerCase().includes(value)
                    );
                });
                setResults(results);
                setEdit(results);
            });
    }

    const handleChange = (value) => {
        setInput(value)
        fetchData(value)
    }
    

    return (
        <div className="seach-container">
            <div className="search" onMouseOver={(e) => e.target.style.background = "#f5f4f4"}>
                <FaSearch id="search-icon"/>
                <input 
                    placeholder="Enter album or title..." 
                    value={input} 
                    onChange={(e) => handleChange(e.target.value)}
                />
            </div>
        </div>     
    )
}
