import {useState, useEffect } from 'react';
const API = "http://localhost:3030/api";

function App() {
    const [tracks, setTracks] = useState([]);
    const [newTrack, setNewTrack] = useState("");
    const [query, setQuery] = useState();
    const [results, setResults] = useState([]);

    useEffect(() => {
        GetTracks();
        
    }, [])

    // haetaan kaikki kappaleet
    const GetTracks = () => {
        fetch(API + "/getall")
        .then(res => res.json())
        .then(data => setTracks(data))
        .catch(err => console.error("Error: ", err));
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log("Tapahtuman aiheutti: ", event.target);
        console.log("Hakusana: ", query);
        GetTracks(query);
      };

    const handleClick = (event) => {
        event.preventDefault();
        console.log("Tapahtuman aiheutti: ", event.target);
        GetTracks();
    };
    
    const GetOneTrack = (query) => {
        fetch(API + "/" + query)
          .then((results) => {
            return results.json();
          })
          .then((data) => {
            console.log("Haun tulokset", data);
            const items = data;
            console.log("One track: ", data);
            setResults([items])
          });
      };



/*     const searchBar = () => {

        const [query, setQuery] = useState('');

        const handleSubmit = (event) => {
            event.preventDefault();
        }

        return (
            <form onSubmit={handleSubmit}>
                <label>Search</label>
                <input 
                    placeholder='Enter title or album'
                    type='submit'
                    value='query'
                    onChange={(event) => setQuery(event.target.value)}
                ></input>
            </form>
        )
    } */

    // albumin kansikuva
    let albumCover;
    const CheckImage = (props) => {
        if (props.album === "Born to Die") {
            albumCover = "borntodie.png";
        } else if (props.album === "Born to Die: The Paradise Edition") {
            albumCover = "paradise.png";
        } else if (props.album === "Ultraviolence") {
            albumCover = "ultraviolence.png";
        } else if (props.album === "Honeymoon") {
            albumCover = "honeymoon.png";
        } else if (props.album === "Lust for Life") {
            albumCover = "lustforlife.png";
        } else if (props.album === "Norman Fucking Rockwell") {
            albumCover = "nfr.png";
        } else if (props.album === "Chemtrails over the Country Club") {
            albumCover = "chemtrails.png";
        } else if (props.album === "Blue Banisters") {
            albumCover = "bluebanisters.png";
        } else if (props.album === "Did You Know That There's a Tunnel Under Ocean Blvd") {
            albumCover = "oceanblvd.png";
        } else {
            albumCover = "placeholder.png";
        }  

        return (
            <img src={albumCover} alt="Album cover"/>    
        )
    }

   /*  const AddTrack = async () => {
        const data = await fetch(API + "add", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                text: newTrack
            })    
        }).then(res => res.json());
    } */

    const DeleteTrack = async id => {
        const data = await fetch(API + "/delete/" + id, { method: "DELETE" })
        .then(res => res.json());

        setTracks(tracks => tracks.filter(track => track._id !== data._id))
    }

    return (
        <div className="App">
            <header className="main-header">
                <h1>LDR MUSIC LIBRARY</h1>
                <h2>What would you like to do?</h2>
            </header>

            <nav className="navbar">
                    <ul className="links">
                        <li>Show all songs</li>
                        <li>Search & edit</li>
                        <li>Add new</li>
                    </ul>
            </nav>
            <section className='search'>
                <form onSubmit={handleSubmit}>
                    <label>Search</label>
                    <input  
                        type='search'
                        value={query}
                        onChange={(event) => setQuery(event.target.value)}
                        name='query' 
                        placeholder='Enter title or album'
                    />
                    <button type="submit"></button>
                </form>
            </section>
            <section className='add'>
                    <h3>Add Track</h3> 
                <form>
                    <label>Title</label>
                    <input type='text'></input>
                    <label>Album</label>
                    <input type='text'></input>
                    <input 
                        type="submit" 
                        value="Submit" className='submit'></input>
                </form>
            </section>
            <section className='content'>
                {tracks.map(track => (
                    <div className='track' key={track._id}>
                        <div className='delete' onClick={() => DeleteTrack (track._id)}>x</div> 
                        <CheckImage album={track.album}/>
                        <div className='description'>
                            <div>Title: {track.name}</div>
                            <div>Artist: {track.artist}</div>
                            <div>Album: {track.album}</div>
                            <div>Year: {track.year}</div>
                        </div>
                    </div>
                ))}
            </section>
        </div>

    );
}

export default App;
